        // RGB point lights + white directional
        Color lc[] = {Color(1,0.2,0.2), Color(0.2,1,0.2), Color(0.2,0.2,1), Color(0.5,0.5,0.5)};
        for (int i = 0; i < 4; i++) {
            lights[i].diffuse(lc[i]);
        }
