        nav().pos(0, 2, 8);
        configureWebAudio(44100, 128, 2, 0);
    }

    void onAnimate(double dt) override {
        time += dt;
        float r = 3.0f, h = 1.5f;
        for (int i = 0; i < 3; i++) {
            float angle = time + i * 2.094f;
            lights[i].pos(r * cos(angle), h, r * sin(angle));
        }
    }

    void onDraw(Graphics& g) override {
        g.clear(0.05f, 0.05f, 0.08f);
        g.depthTesting(true);
        g.material(material);
