        lfo.mod(0.3f);  // Pulse width
        blSaw.freq(baseFreq);
        blSquare.freq(baseFreq);
        buzz.freq(baseFreq);
        buzz.harmonics(12);
        dsf.freq(baseFreq);
        dsf.harmonics(8);
        dsf.freqRatio(1.0);
        dsf.ampRatio(0.5);

        // Envelope
        adsr.attack(0.01f);
        adsr.decay(0.1f);
        adsr.sustain(0.7f);
        adsr.release(0.5f);

        // Waveform display
        waveform.primitive(Mesh::LINE_STRIP);
        for (int i = 0; i < 512; i++) {
            buffer[i] = 0;
            waveform.vertex((i / 511.0f) * 6.0f - 3.0f, 0, 0);
            waveform.color(0.4f, 0.9f, 0.5f);
        }

        nav().pos(0, 0, 5);
        configureWebAudio(44100, 128, 2, 0);
        adsr.reset();
    }

    void onAnimate(double dt) override {
        // Update waveform vertices
        for (int i = 0; i < 512; i++) {
            waveform.vertices()[i].y = buffer[i] * 2.0f;
        }
    }

    void onDraw(Graphics& g) override {
        g.clear(0.08f, 0.08f, 0.12f);

        // Draw waveform
        g.lineWidth(2);
        g.pushMatrix();
        g.translate(0, 0, 0);
        g.draw(waveform);
