        if (k.key() == Keyboard::DOWN) {
            baseFreq = std::max(baseFreq / 1.1f, 27.5f);
            updateFrequencies();
        }
        return true;
    }

    void updateFrequencies() {
        sine.freq(baseFreq);
        lfo.freq(baseFreq);
        blSaw.freq(baseFreq);
        blSquare.freq(baseFreq);
        buzz.freq(baseFreq);
        dsf.freq(baseFreq);
        printf("Frequency: %.1f Hz\n", baseFreq);
