        if (k.key() == Keyboard::UP) {
            cutoff.set(std::min(5000.0f, cutoff.get() + 100.0f));
            std::cout << "Cutoff: " << cutoff.get() << std::endl;
            return true;
        }
        if (k.key() == Keyboard::DOWN) {
            cutoff.set(std::max(100.0f, cutoff.get() - 100.0f));
