        // Draw floor with lighting
        g.lighting(true);
        g.pushMatrix();
        g.translate(0, 0, 0);
        g.rotate(-90, 1, 0, 0);
        g.color(0.3, 0.3, 0.35);
        g.draw(floor);
        g.popMatrix();
